// src/components/ReviewForm/ReviewForm.js
import React, { useState } from 'react';
import './ReviewForm.css';

const ReviewForm = ({ consultationData }) => {
  const [showForm, setShowForm] = useState(false);
  const [rating, setRating] = useState('');
  const [feedback, setFeedback] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission logic here
    alert('Thank you for your feedback!');
    setShowForm(false); // Hide form after submission
  };

  return (
    <div className="review-form-container">
      <h2 className="review-title">Provide Your Feedback</h2>
      <div className="consultation-info">
        <p><strong>Doctor:</strong> {consultationData.doctorName}</p>
        <p><strong>Speciality:</strong> {consultationData.speciality}</p>
        <p><strong>Date:</strong> {consultationData.date}</p>
        <p><strong>Time:</strong> {consultationData.time}</p>
      </div>
      <button className="feedback-button" onClick={() => setShowForm(true)}>
        Give Feedback
      </button>

      {showForm && (
        <form className="feedback-form" onSubmit={handleSubmit}>
          <label htmlFor="rating">Rating (1-5):</label>
          <input
            type="number"
            id="rating"
            min="1"
            max="5"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            required
          />

          <label htmlFor="feedback">Feedback:</label>
          <textarea
            id="feedback"
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            required
          />

          <button type="submit" className="submit-button">Submit Feedback</button>
        </form>
      )}
    </div>
  );
};

export default ReviewForm;
