import React, { useState } from "react";
import ReportsLayout from "../ReportsLayout/ReportsLayout"; // Ensure the path is correct
import "./ProfileCard.css";

const ProfileCard = ({ onProfileUpdate }) => {
  const [showReports, setShowReports] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);

  const toggleReports = () => {
    setShowReports((prev) => !prev);
  };

  const toggleEditForm = () => {
    setShowEditForm((prev) => !prev);
  };

  return (
    <div className="profile-card">
      <img
        src="https://via.placeholder.com/80"
        alt="Profile"
        className="profile-pic"
      />
      <h3>Your Profile</h3>
      {showReports ? (
        <ReportsLayout /> // Ensure ReportsLayout is imported and used correctly
      ) : (
        <div className="profile-details">
          <p><b>Name:</b> Your Name</p>
          <p><b>Phone:</b> Your Phone</p>
          <p><b>Email:</b> Your Email</p>
          <button type="button" onClick={toggleEditForm}>
            {showEditForm ? "Cancel" : "Edit"}
          </button>
          {showEditForm && (
            <form onSubmit={(e) => e.preventDefault()}>
              {/* Replace with your form fields and logic */}
              <label>
                Name
                <input type="text" defaultValue="Your Name" />
              </label>
              <label>
                Phone
                <input type="text" defaultValue="Your Phone" />
              </label>
              <label>
                Email
                <input type="email" defaultValue="Your Email" disabled />
              </label>
              <button type="submit">Save</button>
            </form>
          )}
          <button type="button" onClick={toggleReports}>
            {showReports ? "Hide Reports" : "Show Reports"}
          </button>
        </div>
      )}
    </div>
  );
};

export default ProfileCard;
